{# prettier-ignore #}
---
title: "Effusion Labs: Concept Map"
permalink: "map/index.html"
layout: "base.njk"
status: stable
date: 2025-07-01
certainty: high
importance: structural
maintainer: Effusion Labs (via language model collaboration)
tags:
  - visualization
  - digitalgarden
  - graph
  - connections
  - metadata-mapping
aliases:
  - Concept Graph
  - Effusion Overview Map
  - Map of Ideas
memory_ref:
  - "/content/meta/style-guide/"
  - "/content/meta/methodology/"
  - "[[core-concept]]"
---

{# Intro prose, theme-aware colors #}
<div class="map-intro prose dark:prose-invert max-w-none">
  <p>
    This interactive map visualizes how
    <span class="font-bold text-success">Projects</span>,
    <span class="font-bold text-info">Concepts</span>,
    <span class="font-bold text-primary">Sparks</span>,
    and <span class="font-bold text-warning">Meta</span>
    documents relate across the Effusion Labs knowledge base. Use it to explore how fragments evolve
    into ideas and converge into projects.
  </p>
</div>
{# Card wrapper for the visualization #}
<section class="mt-6 card bg-base-100 border border-base-300 shadow">
  <div class="card-body p-4 sm:p-6">
    <div id="concept-map-container" class="h-[80vh] w-full rounded-box"></div>
  </div>
</section>
{# JSON-LD for SEO/graph data #}
<script type="application/ld+json">{{ collections.nodes | conceptMapJSONLD | safe }}</script>
{# vis-network bundle #}
<script src="https://unpkg.com/vis-network/standalone/umd/vis-network.min.js"></script>
{# Graph renderer that reads daisyUI theme vars #}
<script>
(function () {
  function readVar(names, alpha) {
    var rs = getComputedStyle(document.documentElement);
    for (var i=0; i<names.length; i++) {
      var v = rs.getPropertyValue(names[i]).trim();
      if (v) return alpha ? "hsl(" + v + " / " + alpha + ")" : "hsl(" + v + ")";
    }
    return alpha ? "hsl(0 0% 50% / " + alpha + ")" : "hsl(0 0% 50%)";
  }
  function palette() {
    return {
      base:          readVar(["--b1","--color-base-100"]),
      base2:         readVar(["--b2","--color-base-200"]),
      content:       readVar(["--bc","--color-base-content"]),
      contentSubtle: readVar(["--bc","--color-base-content"], "0.7"),
      border:        readVar(["--bc","--color-base-content"], "0.25"),
      primary:       readVar(["--p","--color-primary"]),
      info:          readVar(["--in","--color-info"]),
      success:       readVar(["--su","--color-success"]),
      warning:       readVar(["--wa","--color-warning"]),
      error:         readVar(["--er","--color-error"])
    };
  }
  var C = palette();

  var allNodesData = {{ collections.nodes | jsonify | safe }};
  var nodes = [];
  var edges = [];
  var linkRegex = /\[\[([^|\]#]+)/g;
  var connectionCount = Object.create(null);

  var categoryGlyphs = { Projects:"📁", Concepts:"📘", Sparks:"💡", Meta:"⚙️" };

  function getNodeVisualProps(url, tags) {
    tags = Array.isArray(tags) ? tags : [];
    var lower = String(url||"").toLowerCase();
    var cat =
      (lower.includes("/projects") || tags.includes("projects")) ? "Projects" :
      (lower.includes("/concepts") || tags.includes("concepts")) ? "Concepts" :
      (lower.includes("/sparks")   || tags.includes("sparks"))   ? "Sparks"   :
      (lower.includes("/meta")     || tags.includes("meta"))     ? "Meta"     : null;

    var borderColor =
      cat==="Projects" ? C.success :
      cat==="Concepts" ? C.info :
      cat==="Sparks"   ? C.primary :
      cat==="Meta"     ? C.warning : C.border;

    var shape =
      cat==="Projects" ? "diamond" :
      cat==="Concepts" ? "hexagon" :
      cat==="Sparks"   ? "star" :
      cat==="Meta"     ? "triangleDown" : "dot";

    return { glyph: categoryGlyphs[cat] || "", borderColor: borderColor, shape: shape };
  }

  allNodesData.forEach(function (page) {
    var props = getNodeVisualProps(page.url, page.data.tags || []);
    nodes.push({
      id: page.url,
      label: (props.glyph ? props.glyph+" " : "") + (page.data.title || page.fileSlug),
      title: "Connections: 0",
      shape: props.shape,
      font: { face: "Space Grotesk", size: 14, color: C.content, strokeWidth: 0 },
      color: {
        background: C.base,
        border: props.borderColor,
        highlight: { background: C.base2, border: props.borderColor },
        hover: { background: C.base, border: props.borderColor }
      }
    });
    connectionCount[page.url] = 0;
  });

  ["Projects","Concepts","Sparks","Meta"].forEach(function (cat) {
    nodes.push({
      id: "category:"+cat.toLowerCase(),
      label: (categoryGlyphs[cat]||"")+" "+cat,
      shape: "box",
      value: 42,
      scaling: { min: 10, max: 50, label:{enabled:true,min:14,max:30} },
      title: "Category: "+cat,
      font: { face:"Space Grotesk", size:16, color:C.content, strokeWidth:0 },
      color: {
        background: C.base,
        border: C.primary,
        highlight: { background: C.base2, border: C.primary },
        hover: { background: C.base, border: C.primary }
      },
      mass: 1,
      physics: true
    });
    connectionCount["category:"+cat.toLowerCase()] = 0;
  });

  allNodesData.forEach(function (page) {
    var content = page.inputContent || "";
    var url = String(page.url||"").toLowerCase();
    var tags = page.data.tags || [];

    var m;
    while ((m = linkRegex.exec(content)) !== null) {
      var needle = m[1].trim().toLowerCase();
      var target = allNodesData.find(function (p) {
        return (p.fileSlug && p.fileSlug.toLowerCase()===needle) ||
               (p.data.title && p.data.title.toLowerCase()===needle) ||
               (p.data.aliases && p.data.aliases.map(function(a){return a.toLowerCase();}).includes(needle));
      });
      if (target) {
        edges.push({from:page.url,to:target.url});
        connectionCount[page.url]++; connectionCount[target.url]++;
      }
    }

    var catId =
      (url.includes("/projects")||tags.includes("projects"))?"category:projects":
      (url.includes("/concepts")||tags.includes("concepts"))?"category:concepts":
      (url.includes("/sparks")||tags.includes("sparks"))  ?"category:sparks":
      (url.includes("/meta")||tags.includes("meta"))      ?"category:meta":null;

    if (catId) {
      edges.push({from:catId,to:page.url});
      connectionCount[catId]++; connectionCount[page.url]++;
    }
  });

  nodes.forEach(function (n) {
    var c = connectionCount[n.id] || 0;
    n.value = c;
    n.title = n.label + "\nConnections: "+c;
  });

  var container = document.getElementById("concept-map-container");
  var network = new vis.Network(container,{nodes:nodes,edges:edges},{
    nodes:{scaling:{min:8,max:30},font:{face:"Space Grotesk",size:14,color:C.content,strokeWidth:0},borderWidth:1},
    edges:{width:1,color:{color:C.border,highlight:C.primary,hover:C.contentSubtle,inherit:"from",opacity:0.85},arrows:{to:{enabled:true,scaleFactor:0.4}},smooth:{type:"continuous"}},
    interaction:{hover:true,tooltipDelay:200,navigationButtons:true,keyboard:true},
    physics:{enabled:true,barnesHut:{gravitationalConstant:-3500,centralGravity:0.05,springLength:180,springConstant:0.04,damping:0.12,avoidOverlap:0.5},stabilization:{enabled:true,iterations:1000,updateInterval:25}}
  });

  function recolor() {
    C = palette();
    var allIds = network.body.data.nodes.getIds();
    var updates = allIds.map(function (id) {
      var isCat = id.startsWith("category:");
      var border = isCat ? C.primary : C.border;
      return {id:id,font:{color:C.content},color:{background:C.base,border:border,highlight:{background:C.base2,border:border},hover:{background:C.base,border:border}}};
    });
    network.body.data.nodes.update(updates);
    network.setOptions({edges:{color:{color:C.border,highlight:C.primary,hover:C.contentSubtle,inherit:"from",opacity:0.85}}});
  }

  new MutationObserver(function(muts){
    muts.forEach(function(m){if(m.attributeName==="data-theme"){recolor();}});
  }).observe(document.documentElement,{attributes:true});

  network.on("hoverNode",function(params){
    var nodeId=params.node;
    var connected=network.getConnectedNodes(nodeId);
    var all=network.body.data.nodes.getIds();
    network.body.data.nodes.update(all.map(function(id){return{id:id,color:{background:C.base2,border:C.border},font:{color:C.contentSubtle}};}));
    network.body.data.nodes.update([nodeId].concat(connected).map(function(id){return{id:id,color:{background:C.base,border:C.primary},font:{color:C.content}};}));
  });
  network.on("blurNode",recolor);
  network.on("click",function(params){if(params.nodes.length>0){var id=params.nodes[0];if(!id.startsWith("category:"))window.location.href=id;}});
})();
</script>

{# prettier-ignore #}
{# src/pages/resume/index.njk #}
---
permalink: "/resume/"
title: "Resume"
layout: "resume.njk"
eleventyExcludeFromCollections: true
---
{# Page content only; the HTML/head lives in resume.njk #}
{% from "components/neon-card.njk" import neonCard %}
{% from "components/resume/expander.njk" import expander %}
<a href="#main" class="skip-link btn btn-sm">Skip to main content</a>
<!-- Top bar -->
<div class="navbar bg-base-100 border-b-2 border-base-300 sticky top-0 z-40 bar-underline">
  <div class="flex-1">
    <a href="{{ '/' | url }}" class="btn btn-ghost text-base font-semibold">{{ resume and resume.name or 'Resume' }}</a>
  </div>
  <div class="flex-none gap-2 items-center">
    <button id="theme-toggle"
            class="btn btn-ghost btn-square no-print"
            type="button"
            aria-pressed="false"
            aria-label="Switch theme"
            title="Toggle theme">Theme</button>
    <button class="btn btn-sm btn-outline no-print hb-anim"
            onclick="window.print()"
            title="Print / Save PDF">Print</button>
  </div>
</div>
<!-- Main grid (layout already wraps with <main id="main">) -->
<div class="hb-sheet grid grid-cols-12 gap-6 sm:gap-8 p-4 sm:p-6">
  <!-- LEFT RAIL -->
  <aside class="col-span-12 lg:col-span-4">
    <div class="space-y-6 lg:sticky lg:top-6">
      {% call neonCard({ intensity: 'lite' }) %}
        <h1 class="text-4xl md:text-5xl font-black tracking-tight title-glow">{{ resume and resume.name or "Your Name" }}</h1>
        <p class="mt-2 text-lg opacity-85 hb-ink-soft">{{ resume and resume.title or "Creative Technologist & Engineer" }}</p>
        <div class="mt-4 flex flex-wrap gap-2 text-sm items-center">
          {% if resume and resume.location %}
            <span class="badge badge-outline rounded-full chip-elevated">{{ resume.location }}</span>
          {% endif %}
          {% if resume and resume.phoneNote %}
            <span class="tooltip tooltip-bottom" data-tip="{{ resume.phoneNote }}">
              <span class="badge badge-outline rounded-full chip-elevated">Phone on request</span>
            </span>
          {% endif %}
          {% if resume and resume.email %}
            <a class="badge badge-outline rounded-full chip-elevated"
               href="mailto:{{ resume.email }}"
               onclick="copyToClipboard('{{ resume.email | escape }}'); return false;"
               title="Copy email">{{ resume.email }}</a>
          {% endif %}
          {% if resume and resume.linkedin %}
            <a class="badge badge-outline rounded-full chip-elevated"
               href="{{ resume.linkedin }}"
               target="_blank"
               rel="noopener">LinkedIn</a>
          {% endif %}
          {% if resume and resume.website %}
            <a class="badge badge-outline rounded-full chip-elevated"
               href="{{ resume.website }}"
               target="_blank"
               rel="noopener">Website</a>
          {% endif %}
          {% if resume and resume.github %}
            <a class="badge badge-outline rounded-full chip-elevated"
               href="{{ resume.github }}"
               target="_blank"
               rel="noopener">GitHub</a>
          {% endif %}
        </div>
        {% if resume and resume.downloadPdf %}
          <div class="mt-6">
            <a href="{{ resume.downloadPdf | url }}"
               class="btn btn-outline btn-sm rounded-full no-print">Download PDF</a>
          </div>
        {% endif %}
      {% endcall %}
      {% if resume and resume.summary %}
        {% call neonCard({ title: "Summary", bodyCls: "prose prose-sm sm:prose-base dark:prose-invert max-w-none", neon: false, frame: 'none', rail: false }) %}
          <p class="mt-2 hb-ink-soft">{{ resume.summary }}</p>
        {% endcall %}
      {% endif %}
      {# Highlights #}
      {% set _hc = resume and resume.highlights and (resume.highlights | length) or 0 %}
      {% if _hc %}
        {% call neonCard({ title: "Highlights", neon: false, frame: 'none', rail: false }) %}
          <div class="mt-3">
            <ul class="space-y-2" role="list">
              {% for s in resume.highlights %}
                <li class="hb-tabrow"
                    role="listitem"
                    {% if loop.index0 == 0 %}data-active="true"{% endif %}>
                  <div class="flex items-baseline justify-between gap-3">
                    <div class="hb-tablabel hb-ink-soft">{{ s.title }}</div>
                    <div class="hb-tabvalue">
                      <span class="kpi-badge text-glow-sm">{{ s.value }}</span>
                    </div>
                  </div>
                  {% if s.sub %}<div class="mt-1 hb-tabsub opacity-80 hb-ink-soft">{{ s.sub }}</div>{% endif %}
                </li>
              {% endfor %}
            </ul>
          </div>
        {% endcall %}
      {% endif %}
      {% if resume and (resume.skills or resume.tools) %}
        {% call neonCard({ title: "Skills & Tools", bodyCls: "cq-root cq-chips", neon: false, frame: 'none', rail: false }) %}
          {% if resume.skills and resume.skills.groups %}
            {% for g in resume.skills.groups %}
              <h3 class="mt-3 text-sm font-semibold">{{ g.name }}</h3>
              <div class="mt-2 flex flex-wrap gap-2">
                {% for item in g.items %}
                  {% if item.hint %}
                    <span class="badge badge-ghost rounded-full chip-elevated tooltip"
                          data-tip="{{ item.hint }}">{{ item.name }}</span>
                  {% else %}
                    <span class="badge badge-ghost rounded-full chip-elevated">{{ item.name }}</span>
                  {% endif %}
                {% endfor %}
              </div>
            {% endfor %}
          {% elif resume.skills %}
            <div class="mt-2 flex flex-wrap gap-2">
              {% for s in resume.skills %}<span class="badge badge-ghost rounded-full chip-elevated">{{ s }}</span>{% endfor %}
            </div>
          {% endif %}
          {% if resume.tools %}
            <h3 class="mt-4 text-sm font-semibold">Tools</h3>
            <div class="mt-2 flex flex-wrap gap-2">
              {% for t in resume.tools %}<span class="badge badge-ghost rounded-full chip-elevated">{{ t }}</span>{% endfor %}
            </div>
          {% endif %}
        {% endcall %}
      {% endif %}
    </div>
  </aside>
  <!-- RIGHT COLUMN -->
  <section class="col-span-12 lg:col-span-8 space-y-6">
    {% if resume and resume.experience %}
      {% call neonCard({ title: "Experience", neon: false, frame: 'none', rail: false }) %}
        <div class="mt-3 hb-list">
          {% for job in resume.experience %}
            <article class="hb-row hb-anim">
              <div class="hb-ex">
                <div class="hb-ex-head">
                  <h3 class="hb-ex-title text-lg font-semibold tracking-tight m-0 title-glow">
                    {{ job.company }}
                    {% if job.role %}<span class="font-normal">— {{ job.role }}</span>{% endif %}
                  </h3>
                  <div class="hb-ex-meta text-xs md:text-sm opacity-90 flex flex-wrap items-center gap-2">
                    {% if job.location %}<span class="meta-chip">{{ job.location }}</span>{% endif %}
                    <span class="meta-chip">{{ job.start }}
                      {% if job.end %}
                        — {{ job.end }}
                      {% else %}
                        — Now
                      {% endif %}
                    </span>
                  </div>
                </div>
                {% if job.highlights %}
                  {% set _maxBul = 5 %}
                  <ul class="hb-ex-bullets mt-2 space-y-1.5 text-[15px] leading-6">
                    {% for h in job.highlights %}
                      {% if loop.index0 < _maxBul %}<li class="hb-minirow hl-line hb-ink-soft">{{ h }}</li>{% endif %}
                    {% endfor %}
                  </ul>
                  {% if job.highlights | length > _maxBul %}
                    <details class="hb-ex-bullets mt-2">
                      <summary class="link text-sm opacity-80">Show {{ job.highlights | length - _maxBul }} more</summary>
                      <ul class="mt-2 space-y-1.5 text-[15px] leading-6">
                        {% for h in job.highlights %}
                          {% if loop.index0 >= _maxBul %}<li class="hl-line hb-ink-soft">{{ h }}</li>{% endif %}
                        {% endfor %}
                      </ul>
                    </details>
                  {% endif %}
                {% endif %}
                {% if job.tags %}
                  <div class="mt-3 flex flex-wrap gap-2 cq-chips">
                    {% for tag in job.tags %}<span class="badge badge-ghost rounded-full chip-elevated">{{ tag }}</span>{% endfor %}
                  </div>
                {% endif %}
              </div>
            </article>
          {% endfor %}
        </div>
      {% endcall %}
    {% endif %}
    {% if resume and resume.projects %}
      {% call neonCard({ title: "Selected Projects", bodyCls: "cq-root", neon: false, frame: 'none', rail: false }) %}
        <div class="mt-3 space-y-3">
          {% for p in resume.projects %}
            {% call expander(p.name or 'Untitled Project', { subtitle: p.subtitle, badge: p.badge, href: p.url }) %}
              {% if p.blurb %}<p class="text-sm leading-6 mt-1.5 hb-ink-soft">{{ p.blurb }}</p>{% endif %}
              {% if p.highlights and (p.highlights | length) %}
                <h4 class="mt-3 text-sm font-semibold">Highlights</h4>
                <ul class="mt-1.5 space-y-1.5 text-sm leading-6">
                  {% for h in p.highlights %}<li class="hl-line hb-ink-soft">{{ h }}</li>{% endfor %}
                </ul>
              {% endif %}
              {% set stack = p.stack or p.tags %}
              {% if stack and (stack | length) %}
                <div class="mt-3 flex flex-wrap gap-2 cq-chips">
                  {% for t in stack %}<span class="badge badge-ghost rounded-full chip-elevated">{{ t }}</span>{% endfor %}
                </div>
              {% endif %}
              {% if p.status %}<div class="mt-3 text-xs opacity-80 hb-ink-soft">{{ p.status }}</div>{% endif %}
            {% endcall %}
          {% endfor %}
        </div>
      {% endcall %}
    {% endif %}
    {% if resume and resume.education %}
      {% call neonCard({ title: "Education", neon: false, frame: 'none', rail: false }) %}
        <div class="mt-3 hb-list">
          {% for e in resume.education %}
            <article class="hb-row hl-line">
              <div class="flex items-baseline justify-between gap-3 flex-wrap">
                <h3 class="text-base font-medium m-0 title-glow">{{ e.school }}</h3>
                <div class="text-sm opacity-90">
                  <span class="meta-chip">{{ e.start }}
                    {% if e.end %}— {{ e.end }}{% endif %}
                  </span>
                </div>
              </div>
              <div class="text-sm opacity-90 mt-1 hb-ink-soft">
                {{ e.degree }}
                {% if e.meta %}— {{ e.meta }}{% endif %}
              </div>
            </article>
          {% endfor %}
        </div>
      {% endcall %}
    {% endif %}
    <footer class="flex items-center justify-between pt-2 pb-10 no-print">
      <small class="opacity-60">
        {% if resume and resume.updated %}Last updated: {{ resume.updated | date("yyyy-MM-dd") }}{% endif %}
      </small>
      <div class="flex items-center gap-3"></div>
    </footer>
  </section>
</div>
<!-- Toaster -->
<div id="toaster" class="toast toast-end z-[100]"></div>
{# JSON-LD (absolute URLs where possible) #}
{% set canonicalPath = "/resume/" | url %}
{% set canonicalHref = (site and site.url) | ternary((site.url + canonicalPath), canonicalPath) %}
{% set homeHref = (site and site.url) | ternary((site.url + ('/' | url)), ('/' | url)) %}
{% if resume and resume.ogImage %}
  {% set ogImageRel = resume.ogImage | url %}
  {% set ogImageHref = (site and site.url) | ternary((site.url + ogImageRel), ogImageRel) %}
{% endif %}
{% set sameAsLinks = [resume and resume.website, resume and resume.linkedin, resume and resume.github] | compactUnique %}
{% set person = {
  "@context": "https://schema.org",
  "@type": "Person",
  "name": resume and resume.name or null,
  "jobTitle": resume and resume.title or null,
  "url": canonicalHref
} %}
{% if ogImageHref %}
  {% set person = person | setAttribute('image', ogImageHref) %}
{% endif %}
{% if resume and resume.address %}
  {% set person = person | setAttribute('address', resume.address) %}
{% elseif resume and resume.location %}
  {% set person = person | setAttribute('address', { "@type":"PostalAddress", "addressLocality": resume.location }) %}
{% endif %}
{% if resume and resume.email %}
  {% set person = person | setAttribute('email', 'mailto:' + resume.email) %}
{% endif %}
{% if sameAsLinks and (sameAsLinks | length) %}
  {% set person = person | setAttribute('sameAs', sameAsLinks) %}
{% endif %}
<script type="application/ld+json">{{ person | json | safe }}</script>
<script type="application/ld+json">
{{ {
  "@context":"https://schema.org",
  "@type":"BreadcrumbList",
  "itemListElement": [
    { "@type":"ListItem","position": 1,"name":"Home","item": homeHref },
    { "@type":"ListItem","position": 2,"name":"Resume","item": canonicalHref }
  ]
} | json | safe }}
</script>
<!-- Page-local helpers -->
<script>
  function toast(msg){
    var wrap=document.getElementById('toaster'); if(!wrap) return;
    var el=document.createElement('div'); el.className='alert alert-info shadow'; el.innerHTML='<span>'+msg+'</span>';
    wrap.appendChild(el); setTimeout(function(){el.style.opacity='0';},2200); setTimeout(function(){el.remove();},2600);
  }
  function copyToClipboard(text){
    try{navigator.clipboard.writeText(text).then(function(){toast('Copied: '+text);});}
    catch(e){
      var t=document.createElement('textarea'); t.value=text; document.body.appendChild(t); t.select();
      try{document.execCommand('copy'); toast('Copied: '+text);}catch(_){} t.remove();
    }
  }
</script>
