src/_includes/components/products/SpecSh:
{# components/products/SpecSheet.njk #}
{% macro specSheet(product) %}
{% set p = product.data %}
{% set releaseDate = p.release_date or (p.release_info and p.release_info.release_date) %}
{% set editionKind = (p.edition and p.edition.kind) %}
{% set editionSize = (p.release_info and p.release_info.edition_size) or (p.availability and p.availability.unit_count) %}
{% set msrpMap  = (p.pricing and p.pricing.original_msrp) %}
{% set secRange = (p.pricing and p.pricing.secondary_market_range) %}
{% set availabilityIsLimited = (p.availability and p.availability.limited) %}
{% if p.market_listings and (p.market_listings | length) %}
  {% set primaryMarket = p.market_listings[0] %}
{% else %}
  {% set primaryMarket = null %}
{% endif %}

<section class="relative card bg-base-100 border border-base-300/60 shadow-xl rounded-2xl overflow-hidden
                before:content-[''] before:pointer-events-none before:block before:absolute before:inset-0
                before:bg-[radial-gradient(1200px_600px_at_0%_-10%,hsl(var(--er)/.10),transparent_40%)]
                after:content-[''] after:pointer-events-none after:block after:absolute after:inset-0
                after:bg-[radial-gradient(900px_500px_at_110%_120%,hsl(var(--p)/.10),transparent_40%)]">
  <div class="card-body gap-4">
    <div class="flex items-start justify-between gap-3">
      <div>
        <h2 class="card-title text-2xl sm:text-3xl tracking-tight">
          {{ p.product_title or (p.series and (p.series ~ ' • ' ~ (p.variant or ''))) or p.title or 'Untitled Product' }}
        </h2>
        <div class="mt-2 flex flex-wrap items-center gap-2">
          {% if p.brand %}<span class="badge badge-outline">{{ p.brand | titleizeSlug }}</span>{% endif %}
          {% if p.line %}<span class="badge badge-outline">{{ p.line | titleizeSlug }}</span>{% endif %}
          {% if p.character %}
            <a class="badge badge-ghost hover:badge-outline transition"
               href="{{ '/archives/character/' | url }}{{ (p.character | slug) }}/">
              {{ p.character | titleizeSlug }}
            </a>
          {% endif %}
          {% if p.series %}<span class="badge badge-ghost">{{ p.series | titleizeSlug }}</span>{% endif %}
          {% if p.form %}<span class="badge badge-ghost">{{ p.form | titleizeSlug }}</span>{% endif %}
        </div>
      </div>
      {% set availBadge = availabilityIsLimited ? 'badge-error' : 'badge-success' %}
      <div class="tooltip tooltip-left" data-tip="Availability">
        <span class="badge {{ availBadge }} badge-lg">
          {{ availabilityIsLimited and 'Limited' or 'Standard' }}
        </span>
      </div>
    </div>

    <div class="stats stats-vertical sm:stats-horizontal w-full bg-base-200/40 rounded-xl border border-base-300/50">
      <div class="stat">
        <div class="stat-title opacity-70">Release</div>
        <div class="stat-value text-xl">
          {% if releaseDate %}{{ releaseDate | humanDate | safe }}{% else %}TBD{% endif %}
        </div>
        {% if p.size and (p.size.h or p.size.w or p.size.d) %}
          <div class="stat-desc">
            {{ p.size.h }}{% if p.size.w %}×{{ p.size.w }}{% endif %}{% if p.size.d %}×{{ p.size.d }}{% endif %} {{ p.size.unit or 'cm' }}
          </div>
        {% endif %}
      </div>

      <div class="stat">
        <div class="stat-title opacity-70">MSRP</div>
        <div class="stat-value text-xl">
          {% if primaryMarket and primaryMarket.price %}
            {{ primaryMarket.currency }} {{ primaryMarket.price }}
          {% elseif msrpMap %}
            {% for ccy, amt in msrpMap %}{% if loop.first %}{{ ccy }} {{ amt }}{% endif %}{% endfor %}
          {% else %}—{% endif %}
        </div>
        {% if secRange %}
          <div class="stat-desc">
            {% for ccy, rng in secRange %}{% if loop.first %}Secondary: {{ ccy }} {{ rng }}{% endif %}{% endfor %}
          </div>
        {% endif %}
      </div>

      <div class="stat">
        <div class="stat-title opacity-70">Region Lock</div>
        <div class="stat-value text-xl">{{ (p.distribution and p.distribution.region_lock) | yesNo }}</div>
        {% if p.distribution and p.distribution.markets and (p.distribution.markets | length) %}
          <div class="stat-desc flex flex-wrap gap-1">
            {% for m in p.distribution.markets %}<span class="badge badge-sm">{{ m }}</span>{% endfor %}
          </div>
        {% endif %}
      </div>
    </div>

    <dl data-testid="spec-sheet" class="grid grid-cols-1 sm:grid-cols-12 gap-x-6 gap-y-3 mt-4 text-sm leading-relaxed">
      {% if p.product_id %}
        <dt class="sm:col-span-3 text-base-content/70">Product ID</dt>
        <dd class="sm:col-span-9">
          <code class="kbd kbd-sm align-middle">{{ p.product_id }}</code>
          <button type="button" class="btn btn-ghost btn-xs ml-2" data-copy="{{ p.product_id }}" aria-label="Copy product ID">Copy</button>
        </dd>
      {% endif %}

      {% if p.variant %}
        <dt class="sm:col-span-3 text-base-content/70">Variant</dt>
        <dd class="sm:col-span-9">{{ p.variant | titleizeSlug }}</dd>
      {% endif %}

      {% if editionKind %}
        <dt class="sm:col-span-3 text-base-content/70">Edition</dt>
        <dd class="sm:col-span-9">
          <span class="badge badge-outline">{{ editionKind | titleizeSlug }}</span>
          {% if editionSize %}<span class="badge badge-ghost ml-2">Size: {{ editionSize }}</span>{% endif %}
        </dd>
      {% endif %}

      {% if p.colorway %}
        <dt class="sm:col-span-3 text-base-content/70">Colorway</dt>
        <dd class="sm:col-span-9">{{ p.colorway | safe }}</dd>
      {% endif %}

      {% if p.articulation %}
        <dt class="sm:col-span-3 text-base-content/70">Articulation</dt>
        <dd class="sm:col-span-9">{{ p.articulation | safe }}</dd>
      {% endif %}

      {% if p.materials and (p.materials.shell or p.materials.plush or p.materials.stuffing) %}
        <dt class="sm:col-span-3 text-base-content/70">Materials</dt>
        <dd class="sm:col-span-9">
          <ul class="list-disc pl-5 space-y-1">
            {% if p.materials.shell %}<li><span class="opacity-70">Shell:</span> {{ p.materials.shell | safe }}</li>{% endif %}
            {% if p.materials.plush %}<li><span class="opacity-70">Plush:</span> {{ p.materials.plush | safe }}</li>{% endif %}
            {% if p.materials.stuffing %}<li><span class="opacity-70">Stuffing:</span> {{ p.materials.stuffing | safe }}</li>{% endif %}
          </ul>
        </dd>
      {% endif %}

      {% if p.packaging and (p.packaging.type or p.packaging.dimensions_cm) %}
        <dt class="sm:col-span-3 text-base-content/70">Packaging</dt>
        <dd class="sm:col-span-9">
          {% if p.packaging.type %}{{ p.packaging.type | safe }}{% endif %}
          {% if p.packaging.dimensions_cm %}<span class="opacity-70"> — {{ p.packaging.dimensions_cm | safe }}</span>{% endif %}
        </dd>
      {% endif %}

      {% if p.market_listings and (p.market_listings | length) %}
        <dt class="sm:col-span-3 text-base-content/70">Market Listings</dt>
        <dd class="sm:col-span-9">
          <ul class="flex flex-wrap gap-2">
            {% for l in p.market_listings %}
              <li class="tooltip" data-tip="{{ l.currency }} {{ l.price }}">
                <span class="badge badge-info badge-outline">
                  {{ l.market }}{% if l.currency or l.price %} · {{ l.currency }} {{ l.price }}{% endif %}
                </span>
              </li>
            {% endfor %}
          </ul>
        </dd>
      {% endif %}

      {% if p.designer %}
        <dt class="sm:col-span-3 text-base-content/70">Designer</dt>
        <dd class="sm:col-span-9">{{ p.designer }}</dd>
      {% endif %}

      {% if p.accessories and (p.accessories | length) %}
        <dt class="sm:col-span-3 text-base-content/70">Accessories</dt>
        <dd class="sm:col-span-9">
          <ul class="list-disc pl-5 space-y-1">
            {% for a in p.accessories %}<li>{{ a | safe }}</li>{% endfor %}
          </ul>
        </dd>
      {% endif %}

      <dt class="sm:col-span-3 text-base-content/70">Region Lock</dt>
      <dd class="sm:col-span-9">{{ (p.distribution and p.distribution.region_lock) | yesNo }}</dd>

      {% if p.provenance_ref %}
        <dt class="sm:col-span-3 text-base-content/70">Provenance</dt>
        <dd class="sm:col-span-9">
          {% set prov = (p.provenance_ref | provenanceSources) %}
          {% if prov and prov.length %}
            <ul class="space-y-1">
              {% for s in prov | slice(0,8) %}
                <li>
                  {% if s.url %}
                    <a class="external-link" href="{{ s.url }}" target="_blank" rel="noopener noreferrer">{{ hostname(s.url) or s.url }}</a>
                  {% endif %}
                  {% if s.title %}<span class="opacity-80"> — {{ s.title | safe }}</span>{% endif %}
                  {% if s.currency and s.price %}<span class="opacity-70"> — {{ s.currency }} {{ s.price }}</span>{% endif %}
                  {% if s.market %}<span class="opacity-70"> — {{ s.market }}</span>{% endif %}
                  {% if s.retrieved_at %}<span class="opacity-60"> ({{ s.retrieved_at }})</span>{% endif %}
                </li>
              {% endfor %}
            </ul>
            <div class="mt-1 text-xs opacity-90 space-x-3">
              <a class="link link-hover" href="{{ p.provenance_ref | provenanceViewerUrl }}">View .jsonl</a>
              <a class="link" href="{{ p.provenance_ref | provenanceDownloadUrl }}" download>Download</a>
              {% if prov.length > 8 %}<span class="opacity-60"> · showing 8 of {{ prov.length }}</span>{% endif %}
            </div>
          {% else %}
            <a class="link link-primary" href="{{ p.provenance_ref | provenanceViewerUrl }}">View .jsonl</a>
            <span class="opacity-60">·</span>
            <a class="link" href="{{ p.provenance_ref | provenanceDownloadUrl }}" download>Download</a>
          {% endif %}
        </dd>
      {% endif %}

      {% if p.confidence is defined %}
        {% set confPct = (p.confidence * 100) | round(0, 'floor') %}
        <dt class="sm:col-span-3 text-base-content/70">Confidence</dt>
        <dd class="sm:col-span-9">
          <progress class="progress progress-error w-56 align-middle mr-2" value="{{ confPct }}" max="100"></progress>
          <span class="opacity-70">{{ confPct }}%</span>
        </dd>
      {% endif %}

      {% if p.notes %}
        <dt class="sm:col-span-3 text-base-content/70">Notes</dt>
        <dd class="sm:col-span-9">{{ p.notes | safe }}</dd>
      {% endif %}
    </dl>

    {% if p.Deltas and (p.Deltas | length) %}
      <div class="mt-4">
        <div class="collapse collapse-arrow border border-base-300/60 bg-base-200/50 rounded-xl">
          <input type="checkbox" />
          <div class="collapse-title text-base font-medium">
            {{ p.Deltas | length }} change{% if (p.Deltas | length) > 1 %}s{% endif %} in this record
          </div>
          <div class="collapse-content">
            <ul class="timeline timeline-vertical">
              {% for d in p.Deltas %}
                <li>
                  <div class="timeline-start">{{ d.field }}</div>
                  <div class="timeline-middle"><span class="badge badge-error badge-sm"></span></div>
                  <div class="timeline-end timeline-box prose prose-sm max-w-none">
                    <p class="m-0"><strong>{{ d.reason }}</strong></p>
                    {% if d.from or d.to %}
                      <pre class="m-0 mt-2 rounded-lg bg-base-300/60 p-3 overflow-x-auto"><code>from: {{ d.from | safe }}
to:   {{ d.to   | safe }}</code></pre>
                    {% endif %}
                  </div>
                </li>
              {% endfor %}
            </ul>
          </div>
        </div>
      </div>
    {% endif %}
  </div>
</section>
{% endmacro %}

src/_includes/components/products/MetaNo:
{# components/products/meta-notes.njk #}
{% macro metaNotes(p) %}
{% if p.notes or p.product_id %}
<div class="meta-notes rounded-md border border-base-content/15 bg-base-100/80 backdrop-blur p-4 mt-4" data-testid="meta-notes" role="note">
  {% if p.notes %}<p class="text-base-content/90">{{ p.notes }}</p>{% endif %}

  {% if p.product_id %}
  <div class="mt-3 flex items-center gap-2">
    <code class="kbd kbd-sm">{{ p.product_id }}</code>
    <button type="button" class="btn btn-ghost btn-xs" data-copy="{{ p.product_id }}" aria-label="Copy product ID">Copy</button>
    <span class="sr-only" aria-live="polite" data-copy-status></span>
  </div>
  <script>
    (function(){
      if (window.__copyBound) return; window.__copyBound = true;
      document.addEventListener('click', function(e){
        var btn = e.target.closest('[data-copy]'); if(!btn) return;
        var id = btn.getAttribute('data-copy');
        navigator.clipboard && navigator.clipboard.writeText(id).then(function(){
          var status = btn.parentElement && btn.parentElement.querySelector('[data-copy-status]');
          if(status){ status.textContent = 'Copied'; setTimeout(function(){ status.textContent = ''; }, 1600); }
        }).catch(function(){});
      }, false);
    })();
  </script>
  {% endif %}
</div>
{% endif %}
{% endmacro %}

src/_includes/components/products/Proven:
{# components/provenance-list.njk #}
{# Usage:
   {{ provenanceList(sources, { limit: 20, fx: fxRates, base: 'USD' }) }}
#}
{% from "components/icons.njk" import icon %}
{% macro provenanceList(sources, opts = {}) %}
{% if sources and sources.length %}
  {% set base = (opts.base or 'USD') | upper %}
  {% set fx   = opts.fx or (build and build.fx) or {} %}
  {% set limit = opts.limit or sources.length %}
  {% set items = sources | slice(0, limit) %}

  <ul class="provenance-list not-prose space-y-2" data-testid="provenance">
    {% for s in items %}
      {% set host = s.url | hostname %}
      {% set cur  = (s.currency or base) | upper %}
      {% set price = null %}
      {% if s.price is defined and s.price != null %}
        {% set price = s.price %}
      {% elif s.price_min is defined and s.price_max is defined %}
        {% set price = (s.price_min + s.price_max) / 2 %}
      {% endif %}

      {% set rate = 1 %}
      {% if cur != base %}
        {% if fx and fx[cur] %}
          {% set rate = fx[cur] %}
        {% else %}
          {% set rate = null %}
        {% endif %}
      {% endif %}
      {% set conv = (price != null and rate != null) ? (price * rate) : null %}
      {% if price == null or rate == null %}{% set conv = null %}{% endif %}

      <li class="rounded-box border-2 border-black bg-base-100 p-3 sm:p-4 shadow-[4px_4px_0_rgba(0,0,0,0.85)]">
        <div class="flex items-start justify-between gap-3">
          <div class="min-w-0">
            <a href="{{ s.url }}" class="link link-hover font-medium break-words">
              {{ s.title or host }}
            </a>
            <div class="mt-1 text-xs flex flex-wrap items-center gap-x-2 gap-y-1 opacity-80">
              <span class="badge badge-ghost badge-xs">{{ host }}</span>
              {% if s.market %}<span class="badge badge-outline badge-xs">{{ s.market }}</span>{% endif %}
              {% if s.retrieved_at %}
                <time datetime="{{ s.retrieved_at }}">{{ s.retrieved_at | humanDate | safe }}</time>
              {% endif %}
            </div>
          </div>

          <div class="text-right whitespace-nowrap">
            {% if price != null %}
              <div class="font-semibold">{{ price | money(cur) }}</div>
            {% else %}
              <div class="opacity-50">—</div>
            {% endif %}
            {% if conv != null and cur != base %}
              <div class="text-xs opacity-80">≈ {{ conv | money(base) }}</div>
            {% endif %}
          </div>
        </div>
      </li>
    {% endfor %}
  </ul>

  {% if sources.length > limit %}
    <details class="mt-3">
      <summary class="cursor-pointer">Show {{ sources.length - limit }} more source(s)</summary>
      <ul class="mt-2 space-y-2">
        {% for s in sources | slice(limit, sources.length - limit) %}
          <li class="text-sm"><a class="link link-hover" href="{{ s.url }}">{{ s.title or (s.url | hostname) }}</a></li>
        {% endfor %}
      </ul>
    </details>
  {% endif %}
{% endif %}
{% endmacro %}

src/_includes/components/products/Market:
{# components/products/MarketTable.njk #}
{# Usage:
   {{ marketTable(product.data.market_listings, { fx: fxRates, base: 'USD', asOf: fxAsOf }) }}
   - fx: map of { 'EUR': 1.09, 'GBP': 1.27, ... } meaning 1 UNIT -> X BASE
   - base: target currency (default 'USD')
   - asOf: optional ISO date or label for the FX set (e.g. '2025-09-10')
#}
{% macro marketTable(listings, opts = {}) %}
{% if listings and listings.length %}
  {% set base = (opts.base or 'USD') | upper %}
  {% set fx   = opts.fx or (build and build.fx) or {} %}
  {% set asOf = opts.asOf or (build and build.fx_asof) %}

  {# Precompute converted values and capture stats #}
  {% set rows   = [] %}
  {% set convs  = [] %}
  {% for l in listings | sort(false, false, 'market') %}
    {% set cur = (l.currency or base) | upper %}
    {# choose a representative price: prefer explicit price; else midpoint of a range if present #}
    {% set price = null %}
    {% if l.price is defined and l.price != null %}
      {% set price = l.price %}
    {% elif l.price_min is defined and l.price_max is defined %}
      {% set price = (l.price_min + l.price_max) / 2 %}
    {% endif %}

    {# conversion rate: if same currency => 1; else use fx[cur] if available #}
    {% set rate = 1 %}
    {% if cur != base %}
      {% if fx and fx[cur] %}
        {% set rate = fx[cur] %}
      {% else %}
        {% set rate = null %}
      {% endif %}
    {% endif %}

    {% set converted = null %}
    {% if price != null and rate != null %}
      {% set converted = price * rate %}
      {% set convs = convs.concat([converted]) %}
    {% endif %}

    {% set rows = rows.concat([{
      market: l.market,
      url: l.url,
      currency: cur,
      price: l.price,
      price_min: l.price_min,
      price_max: l.price_max,
      note: l.note,
      converted: converted
    }]) %}
  {% endfor %}

  {# stats #}
  {% set convCount = convs | length %}
  {% set convMin = null %}
  {% set convMax = null %}
  {% set convSum = 0 %}
  {% if convCount > 0 %}
    {% set convMin = convs[0] %}
    {% set convMax = convs[0] %}
    {% for v in convs %}
      {% if v < convMin %}{% set convMin = v %}{% endif %}
      {% if v > convMax %}{% set convMax = v %}{% endif %}
      {% set convSum = convSum + v %}
    {% endfor %}
  {% endif %}
  {% set convAvg = convCount > 0 ? (convSum / convCount) : null %}
  {# (Nunjucks doesn’t allow ?:—above will be treated as a literal; do explicit) #}
  {% if convCount == 0 %}{% set convAvg = null %}{% endif %}

  <div class="overflow-x-auto rounded-box border-2 border-black bg-base-100 shadow-[6px_6px_0_rgba(0,0,0,0.85)]">
    <table data-testid="market-table" class="table table-sm">
      <caption class="caption-top text-xs font-semibold tracking-wide uppercase text-base-content/80">
        Market Listings
        {% if convCount > 0 %}
          <span class="opacity-70"> · Converted to {{ base }}</span>
          {% if asOf %}<span class="opacity-60"> (as of {{ asOf }})</span>{% endif %}
        {% else %}
          {% if fx and (fx | length) == 0 %}
            <span class="opacity-60"> · no FX available</span>
          {% endif %}
        {% endif %}
      </caption>
      <thead class="sticky top-0 bg-base-200 z-10">
        <tr>
          <th scope="col" class="w-[40%]">Market</th>
          <th scope="col" class="text-right w-[30%]">Listed</th>
          <th scope="col" class="text-right w-[30%]">≈ {{ base }}</th>
        </tr>
      </thead>
      <tbody>
        {% for r in rows %}
          <tr>
            <td class="align-top">
              {% if r.url %}
                <a href="{{ r.url }}" class="link link-hover">{{ r.market }}</a>
              {% else %}
                {{ r.market }}
              {% endif %}
              {% if r.note %}
                <div class="text-xs opacity-70 mt-0.5">{{ r.note }}</div>
              {% endif %}
            </td>

            <td class="text-right align-top whitespace-nowrap">
              {% if r.price is not none %}
                {{ r.price | money(r.currency) }}
              {% elif r.price_min is not none and r.price_max is not none %}
                {{ r.price_min | money(r.currency) }}–{{ r.price_max | money(r.currency) }}
              {% else %}
                —
              {% endif %}
              {% if r.currency != base %}
                <span class="badge badge-ghost badge-xs ml-2">{{ r.currency }}</span>
              {% endif %}
            </td>

            <td class="text-right align-top whitespace-nowrap">
              {% if r.converted is not none %}
                {{ r.converted | money(base) }}
              {% else %}
                <span class="opacity-50">n/a</span>
              {% endif %}
            </td>
          </tr>
        {% endfor %}
      </tbody>

      {% if convCount > 0 %}
      <tfoot>
        <tr>
          <th scope="row" class="text-right">Min · Avg · Max</th>
          <td></td>
          <td class="text-right">
            <span class="font-medium">{{ convMin | money(base) }}</span>
            <span class="opacity-60"> · </span>
            <span class="font-medium">{{ (convSum / convCount) | money(base) }}</span>
            <span class="opacity-60"> · </span>
            <span class="font-medium">{{ convMax | money(base) }}</span>
          </td>
        </tr>
      </tfoot>
      {% endif %}
    </table>
  </div>
{% endif %}
{% endmacro %}
